    uint16 constant MAX_BPS = 10_000;

    uint256 constant USDG_DECIMALS = 18;
    uint256 constant WETH_DECIMALS = 18;

    uint256 constant PRICE_PRECISION = 10e30;
    uint256 constant VARIABLE_INTEREST_MODE = 2;

    ///@dev common storage

    /* solhint-disable var-name-mixedcase */
    uint256 FEE = 1000;

    address keeper;
    IDnGmxSeniorVault dnGmxSeniorVault;
    address feeRecipient;
    uint256 withdrawFeeBps;
    uint256 protocolFee;
    uint256 seniorVaultWethRewards;
    uint256 seniorVaultWethConversionThreshold;
    uint256 wethThreshold;
    uint256 slippageThreshold;

    uint256 depositCap;
